package classwork160407;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 10; i < 10; i++) {
			System.out.println(i);
		}
		int j = 0;
		while (j < 10) {
			System.out.println(j);
			j++;
			
		}
		for (int k = 9; k >= 0; k--) {
			System.out.println(k);
		}
	}

}
